INSERT INTO directors (code, name, description, create_at, last_update) VALUES('AG','Hideo Kojima', 'Hideo Kojima es un diseñador y director de videojuegos japonés. Fue el vicepresidente de Konami Computer Entertainment Japan y actualmente se encuentra a cargo del equipo desarrollador de videojuegos', NOW(), NOW());
INSERT INTO directors (code, name, description, create_at, last_update) VALUES('SM','Shinji Mikami', 'Shinji Mikami es un diseñador de videojuegos japonés. Es conocido por videojuegos como Resident Evil 4, Resident Evil, Dino Crisis, The Evil Within y su última producción Ghostwire: Tokyo', NOW(), NOW());
INSERT INTO directors (code, name, description, create_at, last_update) VALUES('GS','Goichi Suda', 'Goichi Suda, es el director ejecutivo de Grasshopper Manufacture. El número "51" en su apodo, Suda51, es un juego de palabras con las letras de su name. En Japonés, "Go" significa 5 y "ichi", 1', NOW(), NOW());
INSERT INTO directors (code, name, description, create_at, last_update) VALUES('YS','Yū Suzuki', 'Yu Suzuki, es uno de los más destacados creadores y producers de videojuegos de la historia, siendo su especialidad el ámbito arcade. Ha desarrollado prácticamente toda su carrera profesional en Sega, donde comenzó a trabajar en 1983', NOW(), NOW());
INSERT INTO directors (code, name, description, create_at, last_update) VALUES('AH','Akihiro Hino', 'Akihiro Hino es el presidente y director ejecutivo de la empresa desarrolladora de videojuegos japonesa Level-5. ', NOW(), NOW());
INSERT INTO directors (code, name, description, create_at, last_update) VALUES('MA','Michel Ancel', 'Michel Ancel es un desarrollador de videojuegos francés que actualmente trabaja en Ubisoft. Es creador, entre otros, del personaje Rayman. El 13 de marzo de 2006 fue nombrado Caballero de las artes y la literatura por el Primer Ministro francés, junto a Shigeru Miyamoto y Frédérick Raynal', NOW(), NOW());
INSERT INTO directors (code, name, description, create_at, last_update) VALUES('NK','Navid Khonsari', 'Navid Khonsari es un iraní-canadiense, creador, escritor, director y productor de realidad virtual / mixta, cine y novela gráfica de videojuegos.', NOW(), NOW());
INSERT INTO directors (code, name, description, create_at, last_update) VALUES('TH','Tom Hall', 'Tom Hall es un diseñador de juegos mejor conocido por su trabajo con id Software en títulos como Doom y Commander Keen. Asistió a la Universidad de Wisconsin – Madison, donde obtuvo un B.S. en informática', NOW(), NOW());
INSERT INTO directors (code, name, description, create_at, last_update) VALUES('TS','Tim Schafer', 'Timothy Schafer es un diseñador de videojuegos estadounidense. Fundó la empresa Double Fine Productions en enero de 2000, después de una década en LucasArts. Schafer es más conocido en la industria de los videojuegos por sus tramas interesantes.', NOW(), NOW());
INSERT INTO directors (code, name, description, create_at, last_update) VALUES('PWSA','Paul W. S. Anderson', 'Paul William Scott Anderson, más conocido como Paul W. S. Anderson, es un director, guionista y productor de cine británico especializado en películas de ciencia ficción​ y considerado como uno de los creadores del género de películas basadas en videojuegos', NOW(), NOW());
INSERT INTO producers (code, name, description, create_at, last_update) VALUES('AT','Atari', 'Para algunos será injusto el primero puesto y para otros no. Atari significó mucho en el mundo de los videojuegos y al día de hoy sigue siendo de las marcas más reconocidas. Fundada en 1972, nos dio grandes names como el pionero PongyArea 51', NOW(), NOW());
INSERT INTO producers (code, name, description, create_at, last_update) VALUES('KN','Konami', 'Otra de las pioneras japonesas. Creada en 1969, es imposible olvidar el InternationalSuperstar Soccer,Sunset Riders yThe Simpsons', NOW(), NOW());
INSERT INTO producers (code, name, description, create_at, last_update) VALUES('CAP','Capcom', 'Capcom se suma al grupo de las empresas japonesas exitosas. Se fundó en 1979 y desde entonces entregó títulos como Final Fight, Resident Evil y Street Fighter', NOW(), NOW());
INSERT INTO producers (code, name, description, create_at, last_update) VALUES('NAM','Namco', 'Otra de las veteranas entre veteranas. Originalmente fundada en 1955, títulos comoPac-Man, Galaxian y Tekken fueron clásicos de los arcades.', NOW(), NOW());
INSERT INTO producers (code, name, description, create_at, last_update) VALUES('EA','Electronic Arts', 'A pesar de que la monstruosa empresa norteamericana no para de absorber a otras, nos ha dado el FIFA, NBA Livey Need for Speed, entre otros', NOW(), NOW());
INSERT INTO producers (code, name, description, create_at, last_update) VALUES('NIN','Nintendo EAD', 'La productora de Nintendo diseñó juegos para la empresa que se transformaron en íconos. Mario, Zelda y Star Fox son unos ejemplos.', NOW(), NOW());
INSERT INTO producers (code, name, description, create_at, last_update) VALUES('US','Ubisoft', 'A pesar de que para muchos es una empresa moderna ya que la reconocen porAssassins Creed, Far Cry y Tom Clancys Splinter Cell, la compañía francesa se fundó en 1986 y comenzó a desarrollar juegos en 1990', NOW(), NOW());
INSERT INTO producers (code, name, description, create_at, last_update) VALUES('LA','LucasArts', 'George Lucas inició su sueño de crear juegos en 1982 y lanzó clásicos como Maniac Mansion, Monkey Island y Sam & Max.', NOW(), NOW());
INSERT INTO producers (code, name, description, create_at, last_update) VALUES('MW','Midway', 'La hoy desaparecida (pertenece a Warner) productora fue la primera en Estados Unidos desde 1958 y dejó juegos como Mortal Kombat y NBA Jam.', NOW(), NOW());
INSERT INTO producers (code, name, description, create_at, last_update) VALUES('MP','Micro Prose', 'Otra de las empresas norteamericanas fundadas en 1982 que nos dio el Civilization, Railroad Tycoon y la saga X-COM', NOW(), NOW());
INSERT INTO technologies (code, name, description, create_at, last_update) VALUES('PS4S','PlayStation 4 Slim', 'Potente consola con un nuevo y delgado sistema PlayStation®4 de 1TB, un controlador inalámbrico DualShock 4 y una potencia de almacenamiento de 1TB. Tiene un diseño sofisticado y moderno.', NOW(), NOW());
INSERT INTO technologies (code, name, description, create_at, last_update) VALUES('N3DS','Nintendo 3DS', 'Este Nintendo 3DS XL es un poco más grande que el nintendo 3DS común, dándole al usuario una experiencia de interacción mayor. Incluye cargador.', NOW(), NOW());
INSERT INTO technologies (code, name, description, create_at, last_update) VALUES('NSW','Nintendo switch', 'Nintendo Switch, es el nuevo sistema de videojuego para el hogar o la calle, proporciona emociones únicas. Incluye consola Nintendo Switch, muelle con interruptor Nintendo, Joy-Con (L) y Joy-Con (R), accesorios para correa Two Joy-Con y una empuñadura Joy-Con, un cable HDMI y un adaptador AC de Nintendo Switch', NOW(), NOW());
INSERT INTO technologies (code, name, description, create_at, last_update) VALUES('WUD','Wii U Deluxe', 'Esta consola de vídeojuegos tiene 32 GB de almacenamiento interno. Tiene el juego de Mario Kart 8 preinstalado, sin necesidad de usar un disco físico', NOW(), NOW());
INSERT INTO technologies (code, name, description, create_at, last_update) VALUES('XOS','Xbox One S', 'La consola Xbox One S tiene una capacidad de almacenamiento de 500GB. La caja viene con un controlador inalámbrico Xbox, y la descarga completa del juego Minecraft.', NOW(), NOW());
INSERT INTO technologies (code, name, description, create_at, last_update) VALUES('PC','Computador', 'Una computadora es un dispositivo informático que es capaz de recibir, almacenar y procesar información de una forma útil. Una computadora está programada para realizar operaciones lógicas o aritméticas de forma automática.', NOW(), NOW());
INSERT INTO games (code, name, description, create_at, last_update, year, director_id, producer_id, technology_id) VALUES('FN','Fortnite', 'Fortnite es un videojuego del año 2017 desarrollado por la empresa Epic Games, lanzado como diferentes paquetes de software que presentan diferentes modos de juego, pero que comparten el mismo motor general de juego y las mecánicas. Fue anunciado en los Spike Video Game Awards en 2011', NOW(), NOW(), '2016', 1, 1, 1);
INSERT INTO games (code, name, description, create_at, last_update, year, director_id, producer_id, technology_id) VALUES('TLOUP2','The Last of Us Part II', 'The Last of Us Part II es un videojuego de acción-aventura y horror de supervivencia desarrollado por Naughty Dog, publicado por Sony en exclusiva para la PlayStation 4 el 19 de junio de 2020', NOW(), NOW(), '2020', 2, 2, 2);
INSERT INTO games (code, name, description, create_at, last_update, year, director_id, producer_id, technology_id) VALUES('GOT','Ghost of Tsushima', 'Ghost of Tsushima es un videojuego de acción-aventura desarrollado por Sucker Punch Productions y publicado por Sony Interactive Entertainment para PlayStation 4', NOW(), NOW(), '2018', 3, 3, 3);
INSERT INTO games (code, name, description, create_at, last_update, year, director_id, producer_id, technology_id) VALUES('GTA','Grand Theft Auto V', 'Grand Theft Auto V es un videojuego de acción-aventura de mundo abierto desarrollado por el estudio Rockstar North y distribuido por Rockstar Games. Fue lanzado el 17 de septiembre de 2013 para las consolas PlayStation 3 y Xbox 360.​', NOW(), NOW(), '2013', 4, 4, 4);
INSERT INTO games (code, name, description, create_at, last_update, year, director_id, producer_id, technology_id) VALUES('FIFA','FIFA 20', 'FIFA 20 es un videojuego de simulación de fútbol desarrollado por EA Sports, como parte de la serie FIFA de Electronic Arts. Está disponible en las plataformas de PlayStation 4, Xbox One, Microsoft Windows y Nintendo Switch', NOW(), NOW(), '2018', 5, 5, 5);
INSERT INTO games (code, name, description, create_at, last_update, year, director_id, producer_id, technology_id) VALUES('MC','Minecraft', 'Minecraft es un videojuego de construcción, de tipo «mundo abierto» o sandbox creado originalmente por el sueco Markus Persson, ​ y posteriormente desarrollado por su empresa, Mojang AB.', NOW(), NOW(), '2017', 6, 6, 6);
